import { useState } from "react";
import "./LimitForm.css";
function LimitForm() {
  const [values, setValues] = useState({
    upperPrice: 100,
    lowerPrice: 90,
  });
  //   const [upperPrice, setUppePrice] = useState("100");
  //   const [lowerPrice, setLowerPrice] = useState("90");
  // const handleUpperPriceChange = (e) => setUppePrice(e.target.value);
  const handleChange = (e) => {
    e.preventDefault();
    const { name, value } = e.target;
    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };
  return (
    <form className="LimitForm">
      <label htmlFor="upperPrice">상한</label>
      <input
        className="LimitForm-upper"
        name="upperPrice"
        value={values.upperPrice}
        placeholder="상한"
        onChange={handleChange}
      />
      <label htmlFor="lowerPrice">하한</label>
      <input
        className="LimitForm-lower"
        id="lowerPrice"
        name="lowerPrice"
        value={values.lowerPrice}
        placeholder="하한"
        onChange={handleChange}
      />
    </form>
  );
}

export default LimitForm;
