import ApexCharts from "apexcharts";
import { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";
import { ChartDraw } from "./api";

function ChartBox() {
  const [isLoading, setIsLoading] = useState(false);
  const [loadingError, setLoadingError] = useState(null);
  // const options = { method: "GET", headers: { Accept: "application/json" } };
  // fetch(
  //   "https://api.upbit.com/v1/candles/minutes/1?market=KRW-BTC&count=5",
  //   options
  // )
  //   .then((response) => response.json())
  //   .then((response) => console.log(response))
  //   .catch((err) => console.error(err));

  const data = [
    {
      time_close: "2018-04-18T19:16:00",
      open: 8615000,
      high: 8618000,
      low: 8611000,
      close: 8611000,
    },
  ];

  const handleLoad = async () => {
    let result;
    try {
      setLoadingError(null);
      setIsLoading(true);
      result = await ChartDraw();
    } catch (error) {
      setLoadingError(error);
      return;
    } finally {
      setIsLoading(false);
    }
    const { data_test } = result;
    console.log(data_test);
  };

  const handleLoadMore = () => {
    handleLoad();
  };

  useEffect(() => {
    handleLoad();
  }, []);

  return (
    <div>
      {submittingError && <p>{submittingError.message}</p>}
      <ReactApexChart
        type="candlestick"
        options={{
          chart: { type: "candlestick" },
          xaxis: {
            type: "datetime",
            categories: data?.map((price) => price.time_close),
          },
        }}
        series={[
          {
            data: data?.map((price) => {
              return [
                Date.parse(price.time_close),
                price.open,
                price.high,
                price.low,
                price.close,
              ];
            }),
          },
        ]}
      />
    </div>
  );
}

export default ChartBox;
